const{body:body,validationResult:validationResult}=require("express-validator");module.exports={loginValidator:[body("email").notEmpty().withMessage("Email requis").isEmail().withMessage("Format email invalide").normalizeEmail(),body("password").notEmpty().withMessage("Mot de passe requis"),(req,res,next)=>{const errors=validationResult(req);if(console.log(errors),!errors.isEmpty())return res.status(400).json({errors:errors.array()});next()}]};