const mongoose=require("mongoose"),bookSchema=mongoose.Schema({userId:{type:String,required:!0},title:{type:String,required:!0},author:{type:String,required:!0},imageUrl:{type:String,required:!0},year:{type:Number,required:!0},genre:{type:String,required:!0},ratings:[{userId:String,grade:Number}],averageRating:{type:Number,required:!0}});module.exports=mongoose.model("Book",bookSchema);